import "../Beer.css"
import "../ShowBeer.css"
import "../Footer.css"

export default {
  title: 'BeerMe',
  component: Welcome,
};

export const Header = () => <>
  <header className="header-home">
    <h1>BeerMe</h1>
  </header></>

Header.story = {
  name: 'Header',
};

